mapper.insert:
        insert into board (title, content, writer)
        values (#{title}, #{content}, #{writer});
#         selectKey

mapper.select:
alter table board auto_increment = 1;
set @COUNT = 0;
update board set bno = @COUNT:=@COUNT+1 where bno > 0;
select Auto_increment from information_schema.tables where TABLE_SCHEMA = 'BasicSpringMVC' and TABLE_NAME = 'board';
